mapper.insert:
INSERT INTO TBL_MEMBER
(
   MEMBER_CODE
 , MEMBER_NAME
 , PHONE
 , EMAIL
 , MEMBER_ID
 , MEMBER_PWD
 , SMS_YN
 , EMAIL_YN
 , MILEAGE
 , POLICY_YN
 , PRIVATE_YN
 , PROMOTION_YN
 , GRADE_CODE
 , LOGIN_TYPE
 , TEL
 , JOIN_DAY
 , MEMBER_STATUS
)
VALUES
(
   SEQ_MEMBER_CODE.NEXTVAL
 , #{ memberName }
 , #{ phone }
 , #{ email }
 , #{ memberId }
 , #{ memberPwd }
 , #{ smsYn }
 , #{ emailYn }
 , #{ mileage }
 , #{ policyYn }
 , #{ promotionYn }
 , #{ gradeCode }
 , #{ loginType }
 , #{ tel }
 , #{ joinDay }
 , #{ memberStatus }
)

INSERT INTO TBL_ADDRESS
(
  ADDRESS_CODE
, ADDRESS_NAME
, POSTAL_CODE
, MEMBER_CODE
, DEFAULT_ADR
, OPTIONAL_ADR
, DEFAULT_YN
)
VALUES
(
   SEQ_ADDRESS_CODE.NEXTVAL
 , #{ addressCode }
 , #{ addressName }
 , #{ postalCode }
 , #{ memberCode }
 , #{ defaultAdr }
 , #{ optionAdr }
 , #{ defaultYn }
)

mapper.select:
SELECT MEMBER_ID
  FROM TBL_MEMBER
 WHERE MEMBER_NAME = #{ memberName }
   AND PHONE = #{ phone }

SELECT MEMBER_ID
  FROM TBL_MEMBER
 WHERE MEMBER_NAME = #{ memberName }
   AND EMAIL = #{ email }

SELECT MEMBER_PWD
  FROM TBL_MEMBER
 WHERE MEMBER_NAME = #{ memberName }
   AND MEMBER_ID = #{ memberId }

SELECT MEMBER_ID
  FROM TBL_MEMBER
 WHERE MEMBER_NAME = #{ memberName }
   AND EMAIL = #{ email}

mapper.update:
UPDATE
       TBL_MEMBER
   SET PHONE = #{ phone }
     , EMAIL = #{ email}
     , MEMBER_PWD = #{ memberPwd }
     , SMS_YN = #{ smsYn }
     , EMAIL_YN = #{ emailYn }
     , PROMOTION_YN = #{ promotionYn }
     , TEL = #{ tel }
 WHERE MEMBER_STATUS = 'Y'
   AND MEMBER_NO = #{ memberNo }
